mapper.update:
UPDATE review 
SET rv_rating = #{rv_rating},
	rv_contents = #{rv_contents},
	rv_date = now()
WHERE rv_id = #{rv_id}
AND mem_id = #{mem_id}
AND rv_real_id = #{rv_real_id}

mapper.select:

SELECT	FORMAT( IFNULL ( AVG(rv_rating), 0 ), 1 ) score
FROM	review 
WHERE	rv_real_id = #{rv_real_id}
